thusPiAssign("message",{send(e,s="info",n=2500){const a="thuspi-message-"+Date.now(),i=$(`<div id="${a}" data-message-type="${s}" class="message animate-in"><div class="message-inner">${e}</div></div>`);i.appendTo(".message-area"),setTimeout((function(){i.removeClass("animate-in"),setTimeout((function(){i.addClass("animate-out"),setTimeout((function(){i.remove()}),600)}),n)}),10)},changeContent:(e,s)=>`#thuspi-message-${e}`.html(s),info(e){thusPi.message.send(e,"info")},error(e){thusPi.message.send(e,"error")},warning(e){thusPi.message.send(e,"warning")}});