$(document).on("submit","form#login-form",(function(n){n.preventDefault();let i=$(this);i.find("button").showLoading();let e=i.find('[name="username"]').val(),a=i.find('[name="password"]').val();thusPi.api.call("login-verify",{username:e,password:a}).then((function(n){i.find("button").hideLoading(),thusPi.page.load(urlParam("redirect")||"home/main",!0)})).catch((function(){i.find("button").hideLoading(),thusPi.message.error(thusPi.locale.translate("login.invalid_credentials"))}))}));