$(document).on("submit","form#login-form",(function(e){e.preventDefault();let n=$(this);n.find("button").showLoading();let i=n.find('[name="username"]').val(),a=n.find('[name="password"]').val();HomioPi.api.call("login-verify",{username:i,password:a}).then((e=>{n.find("button").hideLoading(),HomioPi.page.load(urlParam("redirect")||"home/main",!0)})).catch((()=>{n.find("button").hideLoading(),HomioPi.message.error(HomioPi.locale.translate("login.invalid_credentials"))}))}));